import React, { Component } from 'react';
import logo from './logo.svg';

class App extends Component {
  constructor(props) {
    super(props)

    this.state = {
      activeAdd: false,
      activeEdit: false,
      newUser: false,
      formValues: [
        {
          id: 0,
          nameChange: "Mr.A",
          ageChange: "37",
          nicknameChange: "A"
        }, 
        {
          id: 1,
          nameChange: "Mr.B",
          ageChange: "22",
          nicknameChange: "B"
        } 
      ]      
    };

    this.handleInputChange = this.handleInputChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleClick = this.handleClick.bind(this);
    this.handleEdit = this.handleEdit.bind(this);
  }
  handleClick(e) {
    this.setState({
      activeAdd: true,
    });
  }
  handleEdit(e) {
    this.setState({
      activeEdit: true,
    });
  }
  handleInputChange(event) {
    let formValues = this.state.formValues;
    let name = event.target.name;
    let value = event.target.value;
    formValues[name] = value;
    this.setState({formValues})
  }
  handleSubmit(e) {
    e.preventDefault();
    this.setState({
      newUser: true,
      activeAdd: false,
    });
  }
  handleSave(e) {
    e.preventDefault();
    this.setState({
      activeEdit: false,
    });
  }
  deleteUser(id){
    const newState = this.state;
    const index = newState.formValues.findIndex(a => a.id === id);

    if (index === -1) return;
    newState.formValues.splice(index, 1);

    this.setState(newState); 
  }
  addRow(e){  
    return(
      <form onSubmit={this.handleSubmit}>
        <table>
          <tbody>
            <tr>
              <td>
                <input type="text" name="nameChange" onChange={this.handleInputChange}/>
              </td>
              <td>
                <select name="ageChange" onChange={this.handleInputChange}>
                  <option value="20">20</option>
                  <option value="21">21</option>
                  <option value="22">22</option>
                  <option value="23">23</option>
                  <option value="24">24</option>
                  <option value="25">25</option>
                  <option value="26">26</option>
                  <option value="27">27</option>
                  <option value="28">28</option>
                  <option value="29">29</option>
                  <option value="30">30</option>
                  <option value="31">31</option>
                  <option value="32">32</option>
                </select>
              </td>
              <td>
                <input type="text" name="nicknameChange" onChange={this.handleInputChange} />
              </td>
              <td>
                <input type="submit" value="Save"/>
                <input type="reset" value="Cancel" />
              </td>
            </tr>
          </tbody>
        </table>
      </form>
    )
  }
  editUser(event){  
    return(
      <tr cclassName="edit" id={this.state.formValues.id} onSubmit={this.handleSave}>
        <td>
          <input type="text" name="nameChange" onChange={this.handleInputChange} value={this.state.formValues.nameChange}/>
        </td>
        <td>
          <select name="ageChange" onChange={this.handleInputChange} value={this.state.formValues.ageChange}>
            <option value="20">20</option>
            <option value="21">21</option>
            <option value="22">22</option>
            <option value="23">23</option>
            <option value="24">24</option>
            <option value="25">25</option>
            <option value="26">26</option>
            <option value="27">27</option>
            <option value="28">28</option>
            <option value="29">29</option>
            <option value="30">30</option>
            <option value="31">31</option>
            <option value="32">32</option>
          </select>
        </td>
        <td>
          <input type="text" name="nicknameChange" onChange={this.handleInputChange} value={this.state.formValues.nicknameChange}/>
        </td>
        <td>
          <input type="submit" value="Save"/>
          <input type="reset" value="Cancel" />
        </td>
      </tr>          
    )
  }
  addUser(e) {
    return (
      <tr id={this.state.formValues.id}>
        <td>
          {this.state.formValues.nameChange}
        </td>
        <td>
          {this.state.formValues.ageChange}
        </td>
        <td>
          {this.state.formValues.nicknameChange}
        </td>
        <td>
          <a className="btn-edit">Edit</a>
          <a className="btn-del" onClick={this.deleteUser(this.state.formValues.id)}>Delete</a>
        </td>
      </tr>
    );   
  }
  render() {
    return (
      <div className="inner-app">
        <header className="header">
          <img src={logo} className="logo" alt="logo" />
          <h1 className="title">Welcome to Pretest</h1>
          <p>By Piyanuch Na Takuatung</p>
        </header>
        <div className="container">
          <table className="table">
            <thead>
              <tr>
                <th>
                  Name
                </th>
                <th>
                  Age
                </th>
                <th>
                  Nickname
                </th>
                <th>
                  Action
                </th>
              </tr>
            </thead>
            <tbody>
              {this.state.activeEdit && this.editUser(this.state.formValues.id)}
              {this.state.formValues.map((user) =>       
                <tr key={user.id}>
                  <td>
                  {user.nameChange}
                  </td>
                  <td>
                    {user.ageChange}
                  </td>
                  <td>
                    {user.nicknameChange}
                  </td>
                  <td>
                    <a className="btn-edit" onClick={this.handleEdit}>Edit</a>
                    <a className="btn-del" onClick={() => this.deleteUser(user.id)}>Delete</a>
                  </td>
                </tr>
              )}
              {this.state.newUser && this.addUser()}
            </tbody>
          </table>
          <div id="row-add">
            {this.state.activeAdd && this.addRow()}
          </div>
          <a className="btn-add" onClick={this.handleClick}>ADD</a>
        </div>
      </div>
    );
  }
}
export default App;
