import React, { Component } from 'react';
import logo from './logo.svg';

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      activeAdd: false,
      newUser: false,
      newValue: "",
      formValues: [
        {
          nameChange: "Mr.A",
          ageChange: "37",
          nicknameChange: "A"
        }, 
        {
          nameChange: "Mr.B",
          ageChange: "22",
          nicknameChange: "B"
        } 
      ]      
    };
    this.handleInputChange = this.handleInputChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleClick = this.handleClick.bind(this);
  }
  handleClick(e) {
    this.setState({
      activeAdd: true,
    });
  }
  handleInputChange(event) {
    let newValue = this.state.newValue;
    this.setState(prevState => ({
      ewValue: [event.target.name: event.target.value, ...prevState.myArray]
    }))
    // this.setState({newValue})

    console.log(newValue);

  }
  handleSubmit(e) {
    this.state.formValues.push(this.state.newValue)
    // this.setState(
    //   this.state
    // )
    // this.state
    console.log(this.state.formValues);
  }
  deleteUser(e){    
    for(var i = 0; i < this.state.formValues.length; i++){
      if(this.state.formValues[i] == e){
         delete this.state.formValues[i]
      }
    }
    this.setState({
      value:this.state.formValues
    })
    console.log(this.state.formValues)
  }
  addRow(e){  
    return(
      <form onSubmit={this.handleSubmit}>
        <table>
          <tbody>
            <tr>
              <td>
                <input type="text" name="nameChange" onChange={this.handleInputChange}/>
              </td>
              <td>
                <select name="ageChange" onChange={this.handleInputChange}>
                  <option value="20">20</option>
                  <option value="21">21</option>
                  <option value="22">22</option>
                  <option value="23">23</option>
                  <option value="24">24</option>
                  <option value="25">25</option>
                  <option value="26">26</option>
                  <option value="27">27</option>
                  <option value="28">28</option>
                  <option value="29">29</option>
                  <option value="30">30</option>
                  <option value="31">31</option>
                  <option value="32">32</option>
                </select>
              </td>
              <td>
                <input type="text" name="nicknameChange" onChange={this.handleInputChange} />
              </td>
              <td>
                <input type="submit" value="Save"/>
                <input type="reset" value="Cancel" />
              </td>
            </tr>
          </tbody>
        </table>
      </form>
    )
  }
  addUser(event) {        
    return (
      <tr key={this.state.formValues}>
        <td>
          {this.state.formValues.nameChange}
        </td>
        <td>
          {this.state.formValues.ageChange}
        </td>
        <td>
          {this.state.formValues.nicknameChange}
        </td>
        <td>
          <input type="button" className="btn-edit" value="Edit" />
          <input type="button" className="btn-del" onClick={this.deleteUser(this.state.formValues)} value="Edit" />
        </td>
      </tr>
    );   
  }
  render() {
    return (
      <div className="inner-app">
        <header className="header">
          <img src={logo} className="logo" alt="logo" />
          <h1 className="title">Welcome to Pretest</h1>
          <p>By Piyanuch Na Takuatung</p>
        </header>
        <div className="container">
          <table className="table">
            <thead>
              <tr>
                <th>
                  Name
                </th>
                <th>
                  Age
                </th>
                <th>
                  Nickname
                </th>
                <th>
                  Action
                </th>
              </tr>
            </thead>
            <tbody>              
              {this.state.formValues.map((user, index) =>       
                <tr key={index}>
                  <td>
                  {user.nameChange}
                  </td>
                  <td>
                    {user.ageChange}
                  </td>
                  <td>
                    {user.nicknameChange}
                  </td>
                  <td>
                    <input type="button" className="btn-edit" value="Edit" />
                    <input type="button" className="btn-del" onClick={() => this.deleteUser(user)} value="Delete" />
                  </td>
                </tr>
              )}
              {this.state.newUser && this.addUser()}
            </tbody>
          </table>
          <div id="row-add">
            {this.state.activeAdd && this.addRow()}
          </div>
          <input type="button" className="btn-add" onClick={this.handleClick} value="ADD" />
        </div>
      </div>
    );
  }
}
export default App;
