import React, { Component } from 'react';
import logo from './logo.svg';

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      activeAdd: false,
      newUser: false,
      newValue: [],
      user: [
        {
          nameChange: "Mr.A",
          ageChange: "37",
          nicknameChange: "A",
          readonly: true
        }, 
        {
          nameChange: "Mr.B",
          ageChange: "22",
          nicknameChange: "B",
          readonly: true
        } 
      ]      
    };
    this.handleInputChange = this.handleInputChange.bind(this);
    this.editInputChange = this.editInputChange.bind(this);
